	Page += "		var TurnOnReleFunc = \"TurnOnRele\";                                                                                                                                                                                                                                                                                                        ";
	Page += "		for (i = 1; i < 9; i++)                                                                                                                                                                                                                                                                                                                   ";
	Page += "		{                                                                                                                                                                                                                                                                                                                                         ";
	Page += "			var xhttp = new XMLHttpRequest();                                                                                                                                                                                                                                                                                                     ";
	Page += "			xhttp.onreadystatechange = function()                                                                                                                                                                                                                                                                                                 ";
	Page += "			{                                                                                                                                                                                                                                                                                                                                     ";
	Page += "			if (this.readyState == 4 && this.status == 200)                                                                                                                                                                                                                                                                                       ";
