	Page += "				document.getElementById(TurnOnReleId+i).innerHTML =                                                                                                                                                                                                                                                                               ";
	Page += "				this.responseText;                                                                                                                                                                                                                                                                                                                ";
	Page += "			}                                                                                                                                                                                                                                                                                                                                     ";
	Page += "			};                                                                                                                                                                                                                                                                                                                                    ";
