	Page += "			}                                                                                                                                                                                                                                                                                                                                     ";
	Page += "			};                                                                                                                                                                                                                                                                                                                                    ";
	Page += "			xhttp.open(\"GET\", TimeReleFunc+i, true);                                                                                                                                                                                                                                                                                              ";
	Page += "			xhttp.send();                                                                                                                                                                                                                                                                                                                         ";
	Page += "			setTimeout(vuota, 1000);                                                                                                                                                                                                                                                                                                              ";
	Page += "		}                                                                                                                                                                                                                                                                                                                                         ";
	Page += "		}                                                                                                                                                                                                                                                                                                                                         ";
