	Page += "			text-align: center;                                                                                                                                                                                                                                                                                                                   ";
	Page += "			border-bottom: 1px solid LightGrey;                                                                                                                                                                                                                                                                                                   ";
	Page += "			font-size:28px;                                                                                                                                                                                                                                                                                                                       ";
	Page += "			font-family:Verdana;                                                                                                                                                                                                                                                                                                                  ";
	Page += "		}                                                                                                                                                                                                                                                                                                                                         ";
	Page += "		tr:hover {background-color:#f5f5f5;}                                                                                                                                                                                                                                                                                                      ";
