	Page += "			color: black;                                                                                                                                                                                                                                                                                                                         ";
	Page += "			text-align:center;                                                                                                                                                                                                                                                                                                                    ";
	Page += "		}                                                                                                                                                                                                                                                                                                                                         ";
	Page += "		.small_text {                                                                                                                                                                                                                                                                                                                             ";
	Page += "			font-size: 70%;                                                                                                                                                                                                                                                                                                                       ";
	Page += "			color: #737373;                                                                                                                                                                                                                                                                                                                       ";
